mapper.update:
UPDATE inventory 
SET available_stock = available_stock - #{quantity},
    frozen_stock = frozen_stock + #{quantity},
    version = version + 1
WHERE product_id = #{productId} 
  AND available_stock >= #{quantity}
  AND version = #{version}

UPDATE inventory 
SET available_stock = available_stock - #{quantity},
    frozen_stock = frozen_stock + #{quantity},
    version = version + 1
WHERE product_id = #{productId} 
  AND available_stock >= #{quantity}
  AND version = #{version}

UPDATE inventory 
SET available_stock = available_stock + #{quantity},
    frozen_stock = frozen_stock - #{quantity},
    version = version + 1
WHERE product_id = #{productId} 
  AND frozen_stock >= #{quantity}
  AND version = #{version}
